import React from "react";

const status_choices = {
  BACKLOG: "Backlog",
  TODO: "To Do",
  INPROGRESS: "In Progress",
  COMPLETED: "Completed",
};

const TaskStatus = ({ taskStatus }) => {
  console.log('taskStatus inside TaskStatus.js: ', taskStatus);
  return <span>
      {taskStatus ? status_choices[taskStatus] : "Unavailable"}
      {console.log('taskStatus inside TaskStatus.js: ', taskStatus)}
    </span>;
};

export default TaskStatus;
