import React, { useRef, useState, useEffect } from "react";
import btnStyles from "../styles/Button.module.css";
import { useHistory, useParams } from "react-router-dom";
import { axiosReq } from "../api/axiosDefaults";
import axios from "axios";
import {
  Form,
  Button,
  Alert
} from "react-bootstrap"

export const StatusUpdateForm = ({ handle}) => {

  const [errors, setErrors] = useState({});
  const [users, setUsers] = useState([]);
  const [taskStatusChoices, setTaskStatusChoices] = useState([{'value': '', 'label': ''}]);


  // Fetch task status choices from the API
  useEffect(() => {
    const fetchTaskStatusChoices = async () => {
      try {
        const response = await axiosReq.get('/status-choices/');
          setTaskStatusChoices(response.data);
      } catch (error) {
          console.error('Error fetching task_status options:', error);
        }
    };
    fetchTaskStatusChoices();
  }, []);


  const [taskData, setTaskData] = useState({
    task_status: '',
    assigned_to: '',
  });
  const {
    task_status,
    assigned_to,
  } = taskData;

  const history = useHistory();
  const {id} = useParams();

  useEffect(() => {
    const handleMount = async () => {
      try {
        const { data } = await axiosReq.get(`/tasks/${id}/`);
        const {
          task_status,
          assigned_to, 
        } = data;

        setTaskData({
          task_status,
          assigned_to,
        })
      } catch (err) {
        // console.log(err);
      }
    };

    handleMount();
  }, [history, id]);

  const handleChange = (event) => {
    setTaskData({
      ...taskData,
      [event.target.name]: event.target.value,
    });
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    const formData = new FormData();

    formData.append('task_status', taskData.task_status);
    formData.append('assigned_to', assigned_to);

    try {
      await axiosReq.put(`/tasks/${id}/`, formData);
      history.push(`/tasks/${id}`);
    } catch (err) {
      // console.log(err);
      if (err.response?.status !== 401) {
        setErrors(err.response?.data);
      }
    }
  };



  const textFields = (
    <div className="text-center">

      {/* Task Status */}
      <Form.Group>
        <Form.Label>Task Status</Form.Label>
        <Form.Control
          as="select"
          name="task_status"
          value={task_status}
          onChange={handleChange}
          aria-label="task status"
        >
          <option value="">Select task status</option>
          {taskStatusChoices.map((statusChoice) => (
            <option key={statusChoice.value} value={statusChoice.value}>{statusChoice.label}</option>
          ))}
        </Form.Control>
      </Form.Group>
      {errors?.task_status?.map((message, idx) => (
        <Alert variant="warning" key={idx}>
          {message}
        </Alert>
      ))}

      <Button
        className={`${btnStyles.Button} ${btnStyles.Blue}`}
        onClick={() => history.goBack()}
      >
        cancel
      </Button>
      <Button className={`${btnStyles.Button} ${btnStyles.Blue}`} type="submit">
        Update
      </Button>
    </div>
  );



  return (

    <Form onSubmit={handleSubmit}>
      {textFields}
    </Form>
  );
};
