import React, { useState, useEffect } from "react";

const status_choices = {
  BACKLOG: "Backlog",
  TODO: "To Do",
  INPROGRESS: "In Progress",
  COMPLETED: "Completed",
};

const TaskStatus = ({ taskStatus }) => {
  const [status, setStatus] = useState(taskStatus);

  useEffect(() => {
    setStatus(taskStatus);
  }, [taskStatus]);
  console.log('status inside TaskStatus.js: ', status);
  return <span>
      {status ? status_choices[status] : "Unavailable"}
      {console.log('status inside TaskStatus.js return: ', status)}
      </span>;
};

export default TaskStatus;

