import React, { useEffect, useState } from "react";

import { Container, Row, Col, Image } from "react-bootstrap";
import Asset from "../../components/Asset";

import styles from "../../styles/ProfilePage.module.css";
import appStyles from "../../App.module.css";

import ProfileList from "./ProfileList";
import { useParams } from "react-router-dom";
import { axiosReq } from "../../api/axiosDefaults";
import { 
  useProfileData, 
  useSetProfileData,
} from "../../contexts/ProfileDataContext";
import InfiniteScroll from "react-infinite-scroll-component";
import Task from "../tasks/Task";
import { fetchMoreData } from "../../utils/utils";
import NoResults from "../../assets/no-results.png"
import { ProfileEditDropdown } from "../../components/MoreDropdown";

function ProfilePage() {
  const [hasLoaded, setHasLoaded] = useState(false);
  // const [profileTasks, setProfileTasks] = useState([]);
  const [profileTasks, setProfileTasks] = useState({ results: [] });
  const [assignedTasks, setAssignedTasks] = useState({ results: [] });
  const { id } = useParams();
  const { setProfileData } = useSetProfileData();
  const { pageProfile } = useProfileData();
  const [profile] = pageProfile.results;

  useEffect(() => {
    const fetchData = async () => {
      try {
        const [
          { data: pageProfile },
          { data: profileTasks },
          { data: assignedTasks }
        ] =
        await Promise.all([
          axiosReq.get(`/profiles/${id}/`),
          axiosReq.get(`/tasks/?owner__profile=${id}`),
          axiosReq.get(`/tasks/?assigned_to=${id}`),
        ]);
        setProfileData((prevState) => ({
          ...prevState,
          pageProfile: {results: [pageProfile]},
        }));
        setProfileTasks(profileTasks);
        setAssignedTasks(assignedTasks);
        setHasLoaded(true);
      } catch(err) {
        console.log(err);
      }
    }
    fetchData();
  }, [id, setProfileData]);

  const mainProfile = (
    <>
      {profile?.is_owner && <ProfileEditDropdown id={profile?.id} />}
      <Row className="px-3 text-center no-gutters">
        <Col lg={3} className="text-lg-left">
          <Image 
            className={styles.ProfileImage}
            roundedCircle 
            src={profile?.image}
          />
        </Col>
        <Col lg={6}>
          <h3 className="m-2">{profile?.owner}</h3>
          <div>{profile?.task_count}</div>

          <div>{profile?.task_count === 1 ? 'task' : 'tasks'}</div>
        </Col>
        {profile?.content && <Col className="p-3">{profile.content}</Col>}
      </Row>
    </>
  );

  const mainProfileTasks = (
    <>
      <hr />
      <p className="text-center">My Tasks</p>
      <hr />
      {profileTasks.results.length ? (
        <InfiniteScroll
          children={profileTasks.results.map((task) => (
            <Task key={task.id} {...task} setTasks={setProfileTasks} />
          ))}
          dataLength={profileTasks.results.length}
          loader={<Asset spinner />}
          hasMore={!!profileTasks.next}
          next={() => fetchMoreData(profileTasks, setProfileTasks)}
        />
      ) : (
        <Asset
          src={NoResults}
          message={`No results found, ${profile?.owner} hasn't posted yet.`}
        />
      )}
    </>
  );

  /* 
    Returns all tasks currently assigned to viewed profiles user
  */
  const mainAssignedTasks = (
    <>
      {assignedTasks.results.length ? (
        <React.Fragment>
          <InfiniteScroll
            children={profileAssigned.results.map((task) => {
              console.log(task);
              if (task.assigned_to === profile.id) {
                return (
                  <Task key={task.id} {...task} setTasks={setProfileAssigned} />
                );
              } else {
                count++;
                return null;
              }
            })}
            dataLength={profileAssigned.results.length}
            loader={<Asset spinner />}
            hasMore={!!profileAssigned.next}
            next={() => fetchMoreData(profileAssigned, setProfileAssigned)}
          />
          {count === profileAssigned.results.length && (
            <Asset
              src={NoResults}
              message={`No results found, ${profile?.owner} has not been assigned any tasks.`}
            />
          )}
        </React.Fragment>
      ) : (
        <Asset
          src={NoResults}
          message={`No results found, ${profile?.owner} has not been assigned any tasks.`}
        />
      )}
    </>
  );

  return (
    <Row>
      <Col className="py-2 p-0 p-lg-2" lg={8}>
        <Container className={appStyles.Content}>
          {hasLoaded ? (
            <>
              <div>Profile Data</div>
              {mainProfile}
              {mainProfileTasks}
            </>
          ) : (
            <Asset spinner />
          )}
        </Container>
      </Col>
      <Col lg={4} className="d-none d-lg-block p-0 p-lg-2">
        <ProfileList />
      </Col>
    </Row>
  );
}

export default ProfilePage;
